import React, { useEffect, useState } from 'react'
import movieFunctions from "../api/index"
import { ReactSession } from 'react-client-session';

// style sheets
import './moviesm.css';

export const MovieSM = (props) => {
    const userid = ReactSession.get("userid");
    const { id } = useParams();
    const [movie, setMovie] = useState([]);
    const [streamMovie, setStreamMovie] = useState([]);
    const [streamingServices, setStreamingServices] = useState([]);
      useEffect(() => {
        movieFunctions
          .getByID({
            'movie_id': id,
            'user_id': userid
          })
          .then((response) => {
              console.log('promise fulfilled')
              let res = response.data
              return res
          })
        .then((res) => {
            setMovie(res.movie)
            setStreamingServices(res.services)
            setStreamMovie(res.streamMovie)
            console.log(res)
          })
      }, []);

    const handleButtonClick = (e) => {
      fetch('http://localhost:3060/authentication/profile/addpin/', {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          'movie_id': id,
          'user_id': userid
        })
      }).then((response) => {
        let req = response.json()
        return req
      })
  }

  const services = streamMovie.map((service)=>{
    console.log(service)
